import React, { useContext, useState, useEffect } from "react";
import {useNavigate} from 'react-router-dom';
import UserContext from "../../store/UserContext";

function AddDeleteUser() {
const navigate = useNavigate()
  const [user, setUser] = useState({
    fname: "",
    lname: "",
    email: "",
    birthDate: "",
    address: "",
    phoneNum: "",
    gender: "",
    pass: "",
  });
  const userCtx = useContext(UserContext);


  const submitHandler = (e) => {
    e.preventDefault();
     userCtx.addUser(user,()=>navigate('/'),(val)=> setShow(val));
  };

  const deleteHandler = ()=>{
    userCtx.removeUser(user,()=> navigate('/'));
  }


    const addClicked = ()=>{
        setShow(true)
    }

  const onChangeHandler = (e) => {
    const fieldName = e.target.name;
    const value = e.target.value;
    const updatedField = { [fieldName]: value };
    setUser((prev) => {
      return {
        ...prev,
        ...updatedField,
      };
    });
  };

  return (
    <div
      style={{
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        flexDirection: "column",
        height: "100vh",
      }}
    >
    <div>
         <button className="addBtn" onClick={addClicked}>
                                                  Add
                                                </button>
                                                <table>
                                                  <tr>
                                                    <th>Name</th>
                                                    <th>Email</th>
                                                    <th>Salary</th>
                                                    <th>Birth Date</th>
                                                    <th>Address</th>
                                                    <th>Gender</th>
                                                    <th>Phone Number</th>
                                                    <th>Type</th>
                                                    <th>Actions</th>
                                                  </tr>
                                                  {staffs.map((staff) => (
                                                    <tr>
                                                      <td>{staff.fname} {staff.lname}</td>
                                                      <td>{staff.email} </td>
                                                      <td>{staff.salary}</td>
                                                      <td>{staff.birth_date}</td>
                                                      <td>{staff.address}</td>
                                                      <td>{staff.gender}</td>
                                                      <td>{staff.phone_number}</td>
                                                      <td>{staff.staffType}</td>
                                                      <td>
                                                        <button
                                                          className="deleteBtn"
                                                          onClick={() => setModal({ showModal: true, id: staff.id })}
                                                        >
                                                          Delete
                                                        </button>
                                                        <button
                                                          className="updateBtn"
                                                          onClick={() => editClicked(staff.id)}
                                                        >
                                                          Edit
                                                        </button>
                                                      </td>
                                                    </tr>
                                                  ))}
                                                </table>
                                                <div className={modal.showModal ? "backdrop" : "backdropNone"}>
                                                  <div className={modal.showModal ? "modal" : "modalNone"}>
                                                    <p>Are you sure you want to delete this staff?</p>
                                                    <div>
                                                      <button onClick={() => deleteClicked(modal.id)}>Yes</button>
                                                      <button onClick={() => setModal({ showModal: false, id: 0 })}>
                                                        No
                                                      </button>
                                                    </div>
                                                  </div>
                                                </div>
                                                  <button onClick={()=> navigate('/addDeleteUser')}>Add or delete a user</button>
                                                </div>


    {show && (
          <form
                action=""
                style={{
                  display: "flex",
                  alignItems: "flex-start",
                  flexDirection: "column",
                  gap: "25px",
                }}
                onSubmit={submitHandler}
              >
                <div style={{ width: "90%" }}>
                  <label htmlFor="fname">First Name: </label>
                  <input
                    type="text"
                    style={{ width: "100%" }}
                    name="fname"
                    onChange={onChangeHandler}
                    value={user.fname}
                  />
                </div>
                <div style={{ width: "90%" }}>
                  {" "}
                  <label htmlFor="lname">LastName: </label>
                  <input
                    style={{ width: "100%" }}
                    type="text"
                    name="lname"
                    onChange={onChangeHandler}
                    value={user.lname}
                  />
                </div>
                <div style={{ width: "90%" }}>
                  {" "}
                  <label htmlFor="email">Email: </label>
                  <input
                    style={{ width: "100%" }}
                    type="email"
                    name="email"
                    onChange={onChangeHandler}
                    value={user.email}
                  />
                </div>
                <div style={{ width: "90%" }}>
                  {" "}
                  <label htmlFor="birthDate">Birth Date: </label>
                  <input
                    style={{ width: "100%" }}
                    type="date"
                    name="birthDate"
                    onChange={onChangeHandler}
                    value={user.birthDate}
                  />
                </div>
                <div style={{ width: "90%" }}>
                  {" "}
                  <label htmlFor="address">Address: </label>
                  <input
                    style={{ width: "100%" }}
                    type="text"
                    name="address"
                    onChange={onChangeHandler}
                    value={user.address}
                  />
                </div>
                <div style={{ width: "90%" }}>
                  {" "}
                  <label htmlFor="phoneNum">Phone Number: </label>
                  <input
                    style={{ width: "100%" }}
                    type="text"
                    name="phoneNum"
                    onChange={onChangeHandler}
                    value={user.phoneNum}
                  />
                </div>
                <div
                  style={{
                    width: "90%",
                    display: "flex",
                    justifyContent: "space-between",
                  }}
                >
                  {" "}
                  <label htmlFor="gender">Gender: </label>
                  <label htmlFor="genderMale">
                    Male
                    <input
                      type="radio"
                      name="gender"
                      id="genderMale"
                      value="Male"
                      onChange={onChangeHandler}
                      checked={user.gender === "Male"}
                    />
                  </label>
                  <label htmlFor="genderFemale">
                    Female
                    <input
                      type="radio"
                      name="gender"
                      id="genderFemale"
                      value="Female"
                      onChange={onChangeHandler}
                      checked={user.gender === "Female"}
                    />
                  </label>
                </div>
                <div style={{ width: "90%" }}>
                  {" "}
                  <label htmlFor="pass">Password: </label>
                  <input
                    style={{ width: "100%" }}
                    type="password"
                    name="pass"
                    onChange={onChangeHandler}
                    value={user.pass}
                  />
                </div>

                <button type="submit">submit</button>
                <button onClick={deleteHandler}>Delete</button>
              </form>
    )}



    </div>
  );
}

export default AddDeleteUser;
