import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

function Home() {
  const navigate = useNavigate();
  const [showModal, setShowModal] = useState(false);
  const [staffs, setStaffs] = useState([
    { id: 1, name: "test" },
    { id: 2, name: "test1" },
    { id: 3, name: "test2" },
    { id: 4, name: "test3" },
    { id: 5, name: "test4" },
    { id: 6, name: "test5" },
  ]);

  const addClicked = () => {
    navigate("/add");
  };

  const deleteClicked = (id) => {
    alert("sure?")
    const updatedStaffs = staffs.filter((staff) => staff.id !== id);
    setStaffs(updatedStaffs);
  };

  const editClicked = (id) => {
    navigate(`/update/${id}`);
  };

  return (
    <div className="container">
      <button className="addBtn" onClick={addClicked}>
        Add
      </button>
      <table>
        <tr>
          <th>ID</th>
          <th>Name</th>
          <th>Actions</th>
        </tr>
        {staffs.map((staff) => (
          <tr>
            <td>{staff.id}</td>
            <td>{staff.name}</td>
            <td>
              <button
                className="deleteBtn"
                onClick={() => deleteClicked(staff.id)}
              >
                Delete
              </button>
              <button
                className="updateBtn"
                onClick={() => editClicked(staff.id)}
              >
                Edit
              </button>
            </td>
          </tr>
        ))}
      </table>
      <div className={showModal ? "modal" : "modalNone"}>
        <p>Are you sure you want to delete this staff?</p>
        <div>
          {" "}
          <button onClick={deleteClicked}>Yes</button>
          <button onClick={() => setShowModal(false)}>No</button>
        </div>
      </div>
    </div>
  );
}

export default Home;
