import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

function Home() {
  const navigate = useNavigate();
  const [staffs, setStaffs] = useState([
    { id: 1, name: "test" },
    { id: 2, name: "test1" },
    { id: 3, name: "test2" },
    { id: 4, name: "test3" },
    { id: 5, name: "test4" },
    { id: 6, name: "test5" },
  ]);

  const addClicked = () => {
    navigate("/add");
  };

  const deleteClicked = (id) => {
    const updatedStaffs = staffs.filter((staff) => staff.id !== id);
    setStaffs(updatedStaffs);
  };

  const editClicked = (id) => {
    navigate(`/update/${id}`);
  };

  return (
    <div className="container">
      <button onClick={addClicked}>Add</button>
      <table>
        <tr>
          <th>ID</th>
          <th>Name</th>
          <th>Actions</th>
        </tr>
        {staffs.map((staff) => (
          <tr>
            <td>{staff.id}</td>
            <td>{staff.name}</td>
            <td>
              <button
                className="deleteBtn"
                onClick={() => deleteClicked(staff.id)}
              >
                Delete
              </button>
              <button
                className="updateBtn"
                onClick={() => editClicked(staff.id)}
              >
                Edit
              </button>
            </td>
          </tr>
        ))}
      </table>
    </div>
  );
}

export default Home;
