import React, { createContext, useState, useEffect } from "react";
import axios from 'axios';

const UserContext = createContext({
  user: {},
  addUser: (
    fname,
    lname,
    email,
    address,
    birthDate,
    phoneNum,
    gender,
    pass
  ) => {},
  removeUser: (userId) => {},
  findUser: (email) => {},
  logUserIn: (email, pass) => {},
  logUserOut: () => {},
});

const initialCurrUserValue = () => {
  const currUser = localStorage.getItem("user");
  return currUser ? JSON.parse(currUser) : {};
};

export const UserContextProvider = (props) => {
  const [loggedInUser, setLoggedInUser] = useState(initialCurrUserValue);

  useEffect(() => {
    localStorage.setItem("user", JSON.stringify(loggedInUser));
  }, [loggedInUser]);

  const addUserHandler = (
    fname,
    lname,
    username,
    email,
    address,
    birthDate,
    phoneNum,
    gender,
    pass
  ) => {
    axios.post('localhost:8080/user/register',{
        data: {
            lname: lname,
            fname: fname,
            username: username,
            password: pass,
            birth_date: birthDate,
            address: address,
            phone_number: phoneNum,
            gender: gender
        }
    }).then(response => {console.log(response)}).catch(err => {console.log(err)})
  };

  const removeUserHandler = (userId) => {
    axios.delete(`localhost:8080/user/${userId}`).then(response => {console.log(response)}).catch(err => {console.log(err)})
  };

  const findUserHandler = (email) => {
    console.log(email);
  };

  const logUserInHandler = (user) => {
    setLoggedInUser(user);
  axios.post('http://127.0.0.1:8080/user/authenticate', {
       username: user.username,
       password: user.password
     })
     .then(response => {
       const token = response.headers['authorization'];
       if (token) {
         localStorage.setItem('jwtToken', token);
         console.log('JWT Token:', token);
       } else {
         console.error('JWT Token not found in response headers');
       }
     })
     .catch(err => {
       console.log(err);
     });
  };

  const logUserOutHandler = () => {
    setLoggedInUser("");
  };

  const context = {
    user: loggedInUser,
    addUser: addUserHandler,
    removeUser: removeUserHandler,
    findUser: findUserHandler,
    logUserIn: logUserInHandler,
    logUserOut: logUserOutHandler,
  };

  return (
    <UserContext.Provider value={context}>
      {props.children}
    </UserContext.Provider>
  );
};

export default UserContext;
